<script>
export default {
  name: "Pop",
  props: {
    show: {
      type: Boolean,
      default: false
    },
    position: {
      type: String,
      default: 'right'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      showPop: false,
      locale: localStorage.getItem('locale')
    }
  },
  watch: {
    show: {
      immediate: true,
      handler(val) {
        this.showPop = val;
      }
    }
  },
  methods: {
    toUrl(type) {
      switch (type) {
        case 'twitter':
          window.location.href = 'https://twitter.com/naboxwallet';
          break;
        case 'telegram':
          window.location.href = 'https://t.me/naboxcommunity';
          break;
        case 'Discord':
          window.location.href = 'https://discord.gg/mQVXZJXMkn';
          break;
      }
    },
    swichLocale() {
      if (this.locale === 'cn') {
        this.$i18n.locale = 'en';
        this.locale = 'en';
        this.$store.commit('changeLang', 'en');
        localStorage.setItem('locale', 'en')
      } else {
        this.$i18n.locale = 'cn';
        this.locale = 'cn';
        this.$store.commit('changeLang', 'cn');
        localStorage.setItem('locale', 'cn')
      }
    },
    swapClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'swap') return false;
      this.$emit('swapClick');
    },
    transferClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'transfer') return false;
      this.$emit('transferClick');
    },
    poolClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'liquidity') return false;
      this.$emit('poolClick');
    },
    vaultsClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'vaults') return false;
      this.$emit('vaultsClick');
    }
  }
}
</script>

