<script>
export default {
  name: 'Pop',
  props: {
    show: {
      type: Boolean,
      default: false
    },
    position: {
      type: String,
      default: 'right'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      showPop: false,
      locale: localStorage.getItem('locale')
    };
  },
  computed: {
    isMobile() {
      return /Android|webOS|iPhone|iPad|BlackBerry/i.test(navigator.userAgent);
    }
  },
  watch: {
    show: {
      immediate: true,
      handler(val) {
        this.showPop = val;
      }
    }
  },
  methods: {
    toUrl(type) {
      switch (type) {
        case 'twitter':
          this.isMobile ? window.location.href = 'https://twitter.com/naboxwallet' : window.open('https://twitter.com/naboxwallet');
          break;
        case 'telegram':
          this.isMobile ? window.location.href = 'https://t.me/naboxcommunity' : window.open('https://t.me/naboxcommunity');
          break;
        case 'Discord':
          this.isMobile ? window.location.href = 'https://discord.gg/mQVXZJXMkn' : window.open('https://discord.gg/mQVXZJXMkn');
          break;
        case 'nerveBridge':
          this.isMobile ? window.location.href = 'https://bridge.nerve.network/' : window.open('https://bridge.nerve.network/');
          break;
        case 'paraBox':
          this.isMobile ? window.location.href = 'https://parabox.nabox.io/' : window.open('https://parabox.nabox.io/');
          break;
        case 'CERTIK':
          this.isMobile ? window.location.href = 'https://drive.google.com/file/d/16p4CZoegq4PsXLfjuw7d9ASpZcaU7sZI/view?usp=sharing' : window.open('https://drive.google.com/file/d/16p4CZoegq4PsXLfjuw7d9ASpZcaU7sZI/view?usp=sharing');
          break;
      }
    },
    switchLocale() {
      if (this.locale === 'cn') {
        this.$i18n.locale = 'en';
        this.locale = 'en';
        this.$store.commit('changeLang', 'en');
        localStorage.setItem('locale', 'en');
      } else {
        this.$i18n.locale = 'cn';
        this.locale = 'cn';
        this.$store.commit('changeLang', 'cn');
        localStorage.setItem('locale', 'cn');
      }
    },
    swapClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'swap') return false;
      this.$emit('swapClick');
    },
    transferClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'transfer') return false;
      this.$emit('transferClick');
    },
    poolClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'liquidityPool') return false;
      this.$emit('poolClick');
    },
    vaultsClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'vaults') return false;
      this.$emit('vaultsClick');
    },
    airdropClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'airdrop') return false;
      this.$emit('airdropClick');
    },
    l1FarmClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'l1farm') return false;
      this.$emit('l1FarmClick');
    },
    l2FarmClick() {
      this.showPop = false;
      if (window.location.hash.split('/')[1] === 'l2farm') return false;
      this.$emit('l2FarmClick');
    }
  }
};
</script>

